header.script:
    $(function () {
      $('.popup-modal').magnificPopup({
        type: 'inline',
        preloader: false,
        focus: '#username',
        modal: true
      });
      $(document).on('click', '.popup-modal-dismiss', function (e) {
        e.preventDefault();
        $.magnificPopup.close();
      });
    });
    $(document).ready(function () {


      // Show login modal when login button is clicked
      // $('#login-button').click(function () {
      //     $('#login-modal').fadeIn();
      // });

      // Handle login form submission
      $('#login-submit').click(function () {
        // Get the entered username and password
        var username = $('#username').val();
        var password = $('#password').val();

        $.ajax({
          url: `https://portal.lpuonline.com/api/WebAPI/ValidateUserLogin?username=${username}&password=${password}`,
          method: 'GET',
          success: function (response) {
            // Parse the JSON response
            var responseData = JSON.parse(response);

            // Extract the URL from the response
            var redirectUrl = responseData.SSO;
            console.log(redirectUrl);
            if (redirectUrl != null)
              // Redirect to the response URL
              window.location.href = redirectUrl.replace('lms', 'staging').replace('SSOAuth',
                'SSOAuth/SsoRedirect');
            else
              $('#error-message').text(responseData.Message);
          },
          error: function (xhr, status, error) {
            // Handle the error
            var errorMessage = 'Error: ' + error;
            $('#error-message').text(errorMessage);
          }
        });

        // alert('Login successful!');
        // $('#login-modal').fadeOut();
      });
    });

